clear% Diagramme P,TSAB_PT=load("fichiers/pSAB_PT.txt");SAC_PT=load("fichiers/pSAC_PT.txt");SBC_PT=load("fichiers/pSBC_PT.txt");SAB=load("fichiers/pSAB.txt");SAC=load("fichiers/pSAC.txt");SBC=load("fichiers/pSBC.txt");% DOMAINDE DE VALIDITE DE LA PHASE 3P3=load("fichiers/P_3.txt");T3=load("fichiers/T_3.txt");V3=load("fichiers/V_3.txt");E3=load("fichiers/E_3.txt");[nP,nT]=size(P3);% Traingle tripleVa=129.905671875844291e-6;  Ea=72.168190127265447e3;Vb=127.666112185063994e-6;  Eb=99.480256119236515e3;Vc=132.538478805442338e-6;  Ec=136.154346228414312e3;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%             POLYGONES  figurehold on% Courbes mesuresfor i=1:nP  plot(P3(i,:)*1e-9,T3(i,:));end% lignes de chgmt de phasesS=SAB_PT;plot(S(:,1)*1e-9,S(:,2), "linewidth",0.5,"color",'black','marker','.');S=SAC_PT;plot(S(:,1)*1e-9,S(:,2), "linewidth",0.5,"color",'black','marker','.');S=SBC_PT;plot(S(:,1)*1e-9,S(:,2), "linewidth",0.5,"color",'black','marker','.');xlabel("P (GPa)");ylabel("T (K)");grid()set(gca, "fontsize", 40)title("Diagramme (P,T)");hold off%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%             POLYGONES    V,Efigurehold on% Un point plot([0.000137230684781]*1e6 ,[6.125506226728779],'marker','+', "linewidth",5)V= 0.000158049; E= 671163;plot([V]*1e6 ,[E],'marker','+', "linewidth",5)% Courbes mesuresfor i=1:nP  plot(V3(i,:)*1e6,E3(i,:));end% Triangleplot([Va,Vb,Vc,Va]*1e6,[Ea,Eb,Ec,Ea],'r');% PloynomesS=SAB;plot(S(:,1)*1e6,S(:,2), "linewidth",0.5,"color",'black','marker','.');S=SAC;plot(S(:,1)*1e6,S(:,2), "linewidth",0.5,"color",'black','marker','.');S=SBC;plot(S(:,1)*1e6,S(:,2), "linewidth",0.5,"color",'black','marker','.');xlabel("V (cm^3/kg)");ylabel("E (J/kg)");grid()set(gca, "fontsize", 40)title("Diagramme (V,E)");hold off