clear all%///////////////             RUNGE-KUTTA               ///////////////////%///////////////    Plot  schema + sol analytique        ///////////////////%%%%%%%%%%%%%%%%%%%%%%  vNRLname=['vNR, q o1 Q+L, equi thermo';       'vNR, q o1 Q+L, cin phase Ttau=1e-9';  ];% symoble pour la legendeSymleg=['.-'; '-.'; '+-';'--', '*-'; 'x-'; 'o-'; 'd-'; 's-';':';'h-';'v-' ];Etot=zeros(1, 1e5);resu=load("sol.txt");[~,n]=size(resu);%size(resu)Xd = resu(1,:); % X décentrée (frontières)Xc = resu(2,1:n-1);tau = resu(3,1:n-1);u = resu(4,:);e = resu(5,1:n-1);p = resu(6,1:n-1);epsilon = resu(7,1:n-1);% energie du sytèmeNRJ=load("NRJ.txt");Nbiter=length(NRJ(1,:));Etot(1:Nbiter)=NRJ(1,:);IMPUL(1:Nbiter)=NRJ(2,:);Mtot(1:Nbiter)=NRJ(3,:);VOL(1:Nbiter)=NRJ(4,:);resu=load("frac_mass.txt");[~,n]=size(resu);fmA=resu(1,1:n-1);fmB=resu(2,1:n-1);fmC=resu(3,1:n-1);figurehold onplot(Xc,epsilon);grid();set(gca, "fontsize", 40);title(['Energie interne spécfique']);xlabel('m')ylabel("E  (J/Kg)");legend(Lname(1,:),'location','northeast');hold offfigurehold onplot(Xc,tau);grid();set(gca, "fontsize", 40);title(['\tau ']);legend(Lname(1,:),'location','southwest');hold offfigurehold onplot(Xc,1./tau);grid();set(gca, "fontsize", 40);title(['\rho ']);legend(Lname(1,:),'location','southwest');hold offfigurehold onplot(Xd,u);grid();set(gca, "fontsize", 40);title(['u ']);legend(Lname(1,:),'location','southwest');hold offfigurehold onplot(Xc,p);grid();set(gca, "fontsize", 40);title(['p ']);legend(Lname(1,:),'location','southwest');hold off% fraction massiquefigurehold onplot(Xc,fmA);grid();set(gca, "fontsize", 40);title(['phase A ']);legend(Lname(1,:),'location','southwest');hold offfigurehold onplot(Xc,fmB);grid();set(gca, "fontsize", 40);title(['phase B ']);legend(Lname(1,:),'location','southwest');hold offfigurehold onplot(Xc,fmC);grid();set(gca, "fontsize", 40);title(['phase C ']);legend(Lname(1,:),'location','southwest');hold off####  % Plot de l'energie du système au cours du temps##  figure##  hold on##  for j=1:ls##    t=0:T/(Nbiter(j)-1):T;##    plot(t,Etot(j,1:Nbiter(j)),'.-');##  end##  grid();##  set(gca, "fontsize", 40);##  title(['energie totale Bizarrium']);##  if ls==1##    legend(Lname(1,:),'location','southwest');##  elseif ls==2##    legend(Lname(1,:),Lname(2,:),'location','southwest');##  elseif ls==3##    legend(Lname(1,:),Lname(2,:),Lname(3,:),'location','southwest');##  elseif ls==4##    legend(Lname(1,:),Lname(2,:),Lname(3,:),Lname(4,:),'location','southwest');##  elseif ls==5##    legend(Lname(1,:),Lname(2,:),Lname(3,:),Lname(4,:),Lname(5,:),'location','southwest');##  elseif ls==6##    legend(Lname(1,:),Lname(2,:),Lname(3,:),Lname(4,:),Lname(5,:),Lname(6,:),'location','southwest');##  elseif ls==7##    legend(Lname(1,:),Lname(2,:),Lname(3,:),Lname(4,:),Lname(5,:),Lname(6,:),Lname(7,:),'location','southwest');##  elseif ls==8##    legend(Lname(1,:),Lname(2,:),Lname(3,:),Lname(4,:),Lname(5,:),Lname(6,:),Lname(7,:),Lname(8,:),'location','southwest');##  elseif ls==9##    legend(Lname(1,:),Lname(2,:),Lname(3,:),Lname(4,:),Lname(5,:),Lname(6,:),Lname(7,:),Lname(8,:),Lname(9,:),'location','southwest');##  elseif ls==10##    legend(Lname(1,:),Lname(2,:),Lname(3,:),Lname(4,:),Lname(5,:),Lname(6,:),Lname(7,:),Lname(8,:),Lname(9,:),Lname(10,:),'location','southwest');##   elseif ls==11##    legend(Lname(1,:),Lname(2,:),Lname(3,:),Lname(4,:),Lname(5,:),Lname(6,:),Lname(7,:),Lname(8,:),Lname(9,:),Lname(10,:),Lname(11,:),'location','southwest');##   endif  ##  hold off##  ##  ##  % Plot de l'impulsion au cours du temps##  figure##  hold on##  for j=1:ls##    t=0:T/(Nbiter(j)-1):T;##    plot(t,IMPUL(j,1:Nbiter(j)),'.-');##  end##  grid();##  set(gca, "fontsize", 40);##  title(['impulsion']);##  if ls==1##    legend(Lname(1,:),'location','southwest');##  elseif ls==2##    legend(Lname(1